import React, { useState, useEffect } from 'react';
import './ctgStyle.css';

const Kpp_Mk = () => {
	const [seragamData, setSeragamData] = useState([]);
	const [apdData, setAPDData] = useState([]);
	const [isLoading, setIsLoading] = useState(true);
	const [error, setError] = useState(null);
	const customerId = 6; 
	const jobId = 11; 
	const baseUrl = 'http://localhost/catalog/app/';

	useEffect(() => {
		fetch(`http://localhost/catalog/conf/api.php?customer_id=${customerId}&job_id=${jobId}`)
		  .then(response => {
			if (!response.ok) {
			  throw new Error('Network response was not ok');
			}
			return response.json();
		  })
		  .then(data => {
			setSeragamData(data.seragam);
			setAPDData(data.apd);
			setIsLoading(false);
		  })
		  .catch(error => {
			setError(error);
			setIsLoading(false);
		  });
	  }, [customerId, jobId]);

	if (isLoading) {
		return <p>Loading...</p>;
	}

	if (error) {
		return <p>Error: {error.message}</p>;
	}

	return (
		<div className="content">
		<h2>Seragam</h2>
		<div className="container catalog_container">
			{seragamData.map(item => (
			<div key={item.id_seragam} className="catalog_left">
				<h3>{item.nama_seragam}</h3>
				<div className="seragam">
				<img className="seragam1" src={`${baseUrl}foto/${item.foto1}`} alt="foto1" />
				{item.foto2 && <img className="seragam2" src={`${baseUrl}foto/${item.foto2}`} alt="foto2" />}
				</div>
				<div className="catalog_content">
				{item.jumlah && (
					<article className="catalog_details">
					<div>
						<small className="text-light">JUMLAH : {item.jumlah}</small>
					</div>
					</article>
				)}
				{item.bahan && (
					<article className="catalog_details">
					<div>
						<small className="text-light">BAHAN : {item.bahan}</small>
					</div>
					</article>
				)}
				{item.logo && (
					<article className="catalog_details">
					<div>
						<small className="text-light">LOGO : {item.logo}</small>
					</div>
					</article>
				)}
				{item.scotlight && (
					<article className="catalog_details">
					<div>
						<small className="text-light">SCOTLIGHT : {item.scotlight}</small>
					</div>
					</article>
				)}
				{item.warna && (
					<article className="catalog_details">
					<div>
						<small className="text-light">WARNA : {item.warna}</small>
					</div>
					</article>
				)}
				</div>
			</div>
			))}
		</div>

		<br></br>

		<h2>APD</h2>
		<div className="container catalog_container">
			{apdData.map(item => (
			<div key={item.id_apd} className="catalog_left">
				<h3>{item.nama_apd}</h3>
				<div className="seragam">
				<img className="seragam1" src={`${baseUrl}foto/${item.foto}`} alt="foto1" />
				</div>
				<div className="catalog_content">
				{item.jumlah && (
					<article className="catalog_details">
					<div>
						<small className="text-light">JUMLAH : {item.jumlah}</small>
					</div>
					</article>
				)}
				{item.merk && (
					<article className="catalog_details">
					<div>
						<small className="text-light">MERK : {item.merk}</small>
					</div>
					</article>
				)}
				{item.type && (
					<article className="catalog_details">
					<div>
						<small className="text-light">TYPE : {item.type}</small>
					</div>
					</article>
				)}
				{item.warna && (
					<article className="catalog_details">
					<div>
						<small className="text-light">WARNA : {item.warna}</small>
					</div>
					</article>
				)}
				</div>
			</div>
			))}
		</div>
		</div>
	);
	
}

export default Kpp_Mk
